mapper.select:
select id, user_id, group_id, suffix, name, create_time, realName
from picture
where group_id = #{groupId}
limit #{start},#{len};

select id, user_id, group_id, suffix, name, create_time, realName
from picture
where group_id = #{groupId}
limit 1;

mapper.insert:
insert into picture
    (user_id, group_id, suffix, name,realName)
values (#{userId}, #{groupId}, #{suffix}, #{name},#{realName});

mapper.update:
update picture
set name = #{pictureName}
where id = #{pictureId};

mapper.select:
select count(*)
from picture
where group_id = #{gourpId};

select id, user_id, group_id, suffix, name, create_time, realName
from picture
where user_id = #{userId}
ORDER BY create_time
limit #{start},#{len};

select id, user_id, group_id, suffix, name, create_time, realName
from picture
where id = #{id};

select id, user_id, group_id, suffix, name, create_time, realName
from picture
where group_id = #{gourpId}
limit #{start},#{len};
